import { useState } from "react";

function FormFinal() {
    const [firstname, setFirstname] = useState("");
    const [lastname, setLastname] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState({
        value: "", 
        isTouched: false});
    const [role, setRole] = useState("role");

    const getIsFormValid = () => {
        return (
            firstname && lastname &&
            validateEmail(email) &&
            password.value.length >= 8 &&
            role !== "role"
        );
    };

    const clearForm = () => {
        setFirstname("");
        setLastname("");
        setEmail("");
        setPassword({
            value: "",
            isTouched: false
        });
        setRole("role");

    };

    const handleSubmit = e => {
        e.preventDefault();
        alert("Account created");
        clearForm();
    };

    return (
        <div className="FormFinal">
            <form>
                <fieldset>
                    <h2>Sign up</h2>
                    <div className="Field">
                        <label htmlFor="firstname">
                            Firstname
                        </label>
                        <input placeholder="Firstname" type="text" id="firstname" name="firstname"/>
                    </div>
                    <div className="Field">
                        <label htmlFor="lastname">
                            Lastname
                        </label>
                        <input placeholder="Lastname" type="text" id="lastname" name="lastname"/>
                    </div>
                    <div className="Field">
                        <label htmlFor="email">
                            Email address
                        </label>
                        <input placeholder="Email" type="email" id="email" name="email"/>
                    </div>
                    <div className="Field">
                        <label htmlFor="password">
                            Password
                        </label>
                        <input placeholder="Password" type="password" id="password" name="password"/>
                    </div>
                    <div className="Field">
                        <label>
                            Role
                        </label>
                        <select>
                            <option value="role">Role</option>
                            <option value="individual">Individual</option>
                            <option value="business">Business</option>
                        </select>
                    </div>
                    <button type="submit" disabled={!getIsFormValid}>Submit</button>
                </fieldset>
            </form>
        </div>
    )
};

export default FormFinal;
