import { List } from "./components/List";
import "./styles/Style.css";
import { useState } from 'react';
import { Todo } from "./components/Todo";
import { Form } from "./components/Form";
import { FormRange } from "./components/FormRange";
import { Routes, Route } from "react-router-dom";
import FormFinal from "./components/FormFinal";


function App() {
  const [todos, setTodos] = useState([
    {
      id: 'todo1',
      createAt: '18:00',
    },
    {
      id: 'todo2',
      createAt: '20:30',
    }
  ]);

  const reverseOrder = () => {
    setTodos([...todos].reverse())
  }

  function Todo() {
    return (
      <div>
        <button onClick={reverseOrder}>Reverse</button>
        <table>
          <tbody>
            {todos.map((todo, index) => (
              <Todo key={todo.id} id={index} createAt={todo.createAt} /> //don't use the "index" for key /
            ))}
          </tbody>
        </table>
      </div>
    )
  }

  return (
    <div>
      <nav>
        <a href="" className="nav-item"></a>
        <a href="" className="nav-item"></a>
        <a href="" className="nav-item"></a>
        <a href="" className="nav-item"></a>
        <a href="" className="nav-item"></a>
      </nav>
      <Route path="/" element={<FormRange />}/>
      <Route path="/" element={<Form />}/>
      <Route path="/" element={<List/>}/>
      <Route path="/" element={<Todo/>}/>
      <Route path="/" element={<FormFinal/>}/>




    </div>
  )
}

export default App;
