        setName(e.target.value)
    }

    const handleSubmit = () => {
        console.log('Form submitted');
    }

    return(
        <div className="Form">
            <form>
                <fieldset>
                    <div className="field">
                        <label>Name</label>
                        <input type="text" placeholder="Name" name="name" onChange={handleChange}/>
                    </div>
                    <button type="submit">Submit</button>
                </fieldset>
            </form>
        </div>
    )
}

