import { useState } from "react"


function FormRange() {
    const [value, setValue] = useState("");
    const [input, setInput] = useState("");


    const chooseNumber = (e) => {
        e.preventDefault();
        setValue(e.target.value)
    }

    const inputValue = (e) => {
        e.preventDefault()
        setInput(e.targetValue)
        value = input
    }    

    return (
        <div className="FormRange">
            <form>
                <fieldset>
                    <div className="field">
                        <label htmlFor="feedback">Form range : </label>
                            <input 
                                onChange={chooseNumber}
                                type="range"
                                name="feedback"
                                id="feedback"
                            /> <p>{value}</p><br/> 
                        <label htmlFor="value">Value : </label>
                            <input
                                type="text"
                                name="value"
                                id="value"
                                placeholder="Enter number"
                            />
                    </div>
                    <button type="submit">Submit</button>
                </fieldset>
            </form>
        </div>
    )
}

export {FormRange}
